#!/bin/bash\n\n# Fix package.json configuration issues\n\necho \"üì¶ Fixing package.json configuration...\"\n\ncd /Users/darrenwalsh/endurance-app\n\n# Create a backup\ncp package.json package.json.backup\n\n# Use node to fix the JSON (safer than sed for JSON)\nnode -e \"\nconst fs = require('fs');\nconst pkg = JSON.parse(fs.readFileSync('package.json', 'utf8'));\n\n// Fix author email (update this with real email)\nif (pkg.author && pkg.author.email === 'your-email@example.com') {\n  pkg.author.email = 'darren@example.com'; // UPDATE THIS\n  console.log('‚úÖ Updated placeholder email');\n}\n\n// Remove duplicate entries\nif (pkg.scripts && pkg.scripts.predeploy && pkg.predeploy) {\n  delete pkg.predeploy;\n  console.log('‚úÖ Removed duplicate predeploy');\n}\n\n// Fix homepage duplication\nif (pkg.scripts && pkg.scripts.homepage) {\n  delete pkg.scripts.homepage;\n  console.log('‚úÖ Removed homepage from scripts');\n}\n\n// Ensure homepage is only in root\nif (!pkg.homepage) {\n  pkg.homepage = 'https://darrenw16.github.io/endurance-app';\n}\n\n// Add prettier script\nif (!pkg.scripts['format']) {\n  pkg.scripts['format'] = 'prettier --write \\\"src/**/*.{js,jsx,ts,tsx,json,css,md}\\\"';\n  pkg.scripts['format:check'] = 'prettier --check \\\"src/**/*.{js,jsx,ts,tsx,json,css,md}\\\"';\n  console.log('‚úÖ Added prettier scripts');\n}\n\nfs.writeFileSync('package.json', JSON.stringify(pkg, null, 2) + '\\n');\nconsole.log('‚úÖ package.json updated');\n\"\n\necho \"üì¶ package.json fixes applied!\"\necho \"üìù NOTE: Update the email address in package.json to your real email\"\n